mapper.update:
MERGE INTO menu_tbl m USING (
SELECT #{item.menu_num} as menu_num, #{item.meal_code} as meal_code, #{item.meal_menu} as meal_menu, #{item.allergy_num} as allergy_num, #{item.menu_edate} as menu_edate, #{item.menu_calorie} as menu_calorie FROM DUAL
) t1 ON(t1.menu_num = m.menu_num) WHEN MATCHED THEN UPDATE SET m.meal_menu = t1.meal_menu, m.menu_calorie = t1.menu_calorie, m.allergy_num = t1.allergy_num, m.menu_wDate = sysdate WHEN NOT MATCHED THEN INSERT(menu_num, staff_num, meal_code, meal_menu, menu_eDate, menu_calorie, allergy_num, menu_wDate) VALUES(get_menu_seq, 3, t1.meal_code, t1.meal_menu, t1.menu_edate, t1.menu_calorie, t1.allergy_num, sysdate)

mapper.select:
SELECT meal_code, meal_menu, allergy_num
FROM menu_tbl
WHERE menu_edate = #{today_date}
